commits.content:
.filmgenre {
    display: flex;
    gap: 0.625rem;
    width: 100%;
    text-align: center;
    overflow: scroll;
    scroll-snap-type: x mandatory;
    overflow: hidden;
}

.hide {
    display: none;
}

.filmgenre::-webkit-scrollbar {
    display: none;
}

.changeslideleft {
    position: absolute;
    left: 0rem;
    width: 1.25rem;
    height: 10.5rem;
    object-fit: scale-down;
}

.changeslideright {
    position: absolute;
    right: 0rem;
    width: 1.25rem;
    height: 10.5rem;
    object-fit: scale-down;
    padding: 0.3125rem;
    rotate: calc(180deg);
}

.homeText {
    display: flex;
    flex-direction: column;
    min-width: 18.75rem;
    max-width: 37.5rem;
    row-gap: 1.25rem;
    padding: 5.625rem 1.875rem 3.75rem;
}

.heading {
    font-weight: 900;
    font-size: 2.75rem;
}

.headingText {
    color: rgb(241, 241, 241);
    font-weight: 100;
    font-size: 1.125rem;
}

.movies {
    display: flex;
    flex-direction: column;
    padding: 1.875rem;
    row-gap: 1.25rem;
}

.blured {
    filter: blur(0.625rem)
}

.goToReg {
    background-color: rgb(17, 17, 17);
    filter: drop-shadow(0rem -5.0rem 1.25rem rgb(17, 17, 17));
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    min-height: 18.75rem;
    justify-content: center;
}

.goToRegInner {
    width: 60%;
}

.filmlink{
    text-decoration: none;
}
