commits.content:
.login {
  background-image: url("../../../public/login.jpg");
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  background-color: black;
  min-height: 100vh;
}

.inner {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: rgba(0, 0, 0, 0.363);
}

.form{
  background-color: rgba(0, 0, 0, 0.719);
  margin-top: 50px;
  padding: 3rem clamp(0rem, 8%, 4.75rem);
}

.innerform {
  display: flex;
  background-color: rgba(0, 0, 0, 0.719);
  flex-direction: column;
  row-gap: 10px;
  padding: 100px 125px;
  width: 220px;
  height: 275px;
  text-align: center;
}



.loginLabel {
  position: relative;
  text-align: left;
  height: 0px;
  top: -42px;
  left: 10px;
  color: rgb(129, 129, 129);
  pointer-events: none;
  transition: all 0.3s ease;
}

.loginInput {
  background-color: rgba(0, 0, 0, 0.7);
  padding: 20px 10px 5px;
  font-size: 13.4px;
  outline: none;
  border: 1px solid #ccc;
  border-radius: 4px;
}

.loginInput:focus+label {
  font-size: 10px;
  top: -47px;
}

.loginInput:not(:placeholder-shown)+label {
  font-size: 10px;
  top: -47px;
}

.loginInput:-webkit-autofill {
  font-size: 13.4px;
  padding: 20px 10px 5px !important;
  -webkit-text-fill-color: rgb(255, 255, 255) !important;
  -webkit-background-clip: text;
}

.loginInput:-webkit-autofill:not(:placeholder-shown)+label {
  font-size: 10px;
  top: -47px;
}

.invalid {
  border: solid red 2px;
}

.caught {
  color: red;
}
